mapper.delete:
DELETE FROM BOARD
WHERE BOARD_TYPE = #{boardType} AND
BOARD_NUM = #{boardNum}

mapper.select:
SELECT BOARD_TYPE ,CODE_NAME ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT FROM ( SELECT b.BOARD_TYPE ,c.CODE_NAME ,b.BOARD_NUM ,b.BOARD_TITLE ,b.BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY b.BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD b INNER JOIN COM_CODE c ON b.BOARD_TYPE = c.CODE_ID WHERE b.BOARD_TYPE IN
#{item}
) WHERE NUMROW BETWEEN (#{pageVo.pageNo}-1)*10 + 1 AND (#{pageVo.pageNo})*10
